import React, {Component} from 'react';
import './SignUpPage.css';



class SignUpPage extends Component{

    constructor(props) {
        super(props)

        this.state= {
            firstName: '',
            lastName: '',
            password: '',
            gender: '',
        }
        this.handleSubmit = this.handleSubmit.bind(this)
    }
    firsthandler = (event) => {
        this.setState({
            firstName: event.target.value
        })
    }
    lasthandler = (event) => {
        this.setState({
            lastName: event.target.value
        })
    }
    passwordhandler = (event) => {
        this.setState({
            password:event.target.value
        })
    }
    genderhandler = (event) => {
        this.setState({
            gender: event.target.value
        })
    }
    handleSubmit= (event) => {
        alert(`${this.state.firstName} ${this.state.lastName} Registered Sucessfully`)
        console.log(this.state);
        this.setState({
            firstName: "",
            lastName: '',
            password: '',
            gender: '',
        })
        event.preventDefault()
    }
    render() {
        return(
        <div>
            <form onSubmit = {this.handleSubmit}>
                <h1> Sign Up</h1>
                <label>FirstName :</label> <input type= 'text' value = {this.state.firstName} onChange={this.firsthandler} placeholder= 'FirstName...'/><br />
                <label>LastName :</label> <input type= 'text' value = {this.state.lastName} onChange = {this.lasthandler} placehoilder = 'LastName...'/><br />
                <label>Password :</label> <input type = 'password' value={this.state.password} onChange={this.passwordhandler} placeholder= 'Password...'/><br />
                <label>Gender :</label><select onChange= {this.genderhandler} defaultValue= 'Select Gender'>
                    <oprtion defaultValue>Select Gender</oprtion>
                    <option value='male'>Male</option>
                    <option value='female'>Female</option>
                </select><br />
                <input type = 'submit' value= 'Submit' />
            </form>
        </div>
        )
    }
        
}

export default SignUpPage
