import React, {Component, View, Story, Button} from 'react';
import './NewsFeed.css';

class NewsFeed extends Component{
    render(){
        var stories = this.props.viewer.stories;
        return(
            <View>
                {stories.map(story => <Story story={story} />)}
                <Button onClick={() => this.loadMore()}>Load More</Button>
            </View>
        );
    }
    loadMore(){
        /// read current params//
        var count = this.pops.relay.variables.count;
        //update params
        this.props.relay.setVariables({
            count:count + 5,
        })

    }
}

export default  NewsFeed;
